        .add({
          offset: '-=2500',
          targets : ".intro-elem .fullstack_elem path",
          strokeWidth: 0,
          opacity : 1,
          translateY : 0,
          delay: function(target, index) {
            // 100ms delay multiplied by every div index, in ascending order
            return index * 100;
          },
      
        })
        .add({
          targets :  ".intro-elem",
           translateX : "-30%",
