</template>

<script>
import Logo from '~/components/Logo.vue'
import VuetifyLogo from '~/components/VuetifyLogo.vue'

export default {
  mounted() {
    this.animate_intro();
  },
  methods : {
    animate_intro() {
       let _this = this;
      this.$anime
        .timeline()
        .add(this.animate_strok(".intro-elem .hi_elem path"))
        .add({
          targets : ".intro-elem .hi_elem",
          easing: 'easeOutExpo',
          translateY : -20,
          scale : 0.3,
        }).add({
          targets : [".intro-elem .name_elem" , ".intro-elem .fullstack_elem"],
          translateY : -20,
          opacity: [
              { value: 1, easing: 'easeInOutSine' }
          ],
        })
        .add(this.animate_strok([".intro-elem .name_elem path" , ".intro-elem .fullstack_elem path"]))
        .add({
          targets : ".intro-elem .name_elem path",
          strokeWidth : 0,
        })
        .add({
          targets : ".intro-elem .fullstack_elem path",
          fill :"#ff1a4e",
        })
    },
    animate_strok(target) {
       let _this = this;
      return {
          targets: target,
          strokeDashoffset: [_this.$anime.setDashoffset, 0],
          easing: 'easeInOutSine',
          duration: 2500,
          delay: function(el, i) { return i * 250 },
          direction: 'alternate',
          strokeWidth: 1,
          fill : function(el,i) {
            if (i == 0 || i == 12 || i == 3) {
              return "#ff1a4e";
            } else {
              return "#fff";
            }
          },
          delay: function(target, index) {
            // 100ms delay multiplied by every div index, in ascending order
            return index * 100;
          },
          elasticity: function(target, index, totalTargets) {
            // Elasticity multiplied by every div index, in descending order
            return 200 + ((totalTargets - index) * 200);
          },
        };
    }
  },
  components: {
    Logo,
    VuetifyLogo,

  }
}
</script>


