    svg.hi_elem {
      max-width: 200px;
    }
    svg.name_elem {
          max-width: 850px;
          opacity: 0;
          stroke-width: 8;
    }
    svg.fullstack_elem{
          max-width: 400px;
          opacity: 0;
              margin-top: 40px;
    }
}
